
public class simplecalculations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 1;
		int b = 2;
		
		System.out.println("사칙연산, a = 1, b = 2");
		System.out.println("a + b = "+ (a+b));	// like python or c#
		System.out.println("a - b = "+ (a-b));
		System.out.println("a * b = "+ (a*b));
		System.out.println("a / b = "+ (a/b));
		System.out.println("a % b = "+ (a%b));
	}

}
